import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import axios from 'axios';
import CreateUser from './CreateUser';
import CreateSalary from './CreateSalary';
import './App.css';

const App = () => {
  const [token, setToken] = useState(localStorage.getItem('token') || '');
  const [role, setRole] = useState(localStorage.getItem('role') || '');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [salaries, setSalaries] = useState([]);
  const [errors, setErrors] = useState({});

  const login = async () => {
    try {
      if (!username || !password) {
        setErrors({
          username: !username ? 'اسم المستخدم مطلوب' : '',
          password: !password ? 'كلمة المرور مطلوبة' : '',
        });
        return;
      }
      const res = await axios.post('http://54.227.197.41:5000/api/login', { username, password });
      setToken(res.data.token);
      setRole(res.data.role);
      localStorage.setItem('token', res.data.token);
      localStorage.setItem('role', res.data.role);
      setErrors({});
    } catch (err) {
      console.error('خطأ تسجيل الدخول:', err);
      alert('فشل تسجيل الدخول: ' + (err.response?.data?.message || err.message));
    }
  };

  const logout = () => {
    setToken('');
    setRole('');
    localStorage.removeItem('token');
    localStorage.removeItem('role');
  };

  const fetchSalaries = async () => {
    try {
      const url = role === 'admin' ? '/api/salaries' : '/api/salaries/user';
      const res = await axios.get(`http://54.227.197.41:5000${url}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setSalaries(res.data);
    } catch (err) {
      console.error('خطأ في جلب المرتبات:', err);
      alert('خطأ في جلب المرتبات: ' + (err.response?.data?.message || err.message));
    }
  };

  const deleteSalary = async (id) => {
    try {
      await axios.delete(`http://54.227.197.41:5000/api/salaries/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('تم حذف المرتب بنجاح');
      fetchSalaries();
    } catch (err) {
      console.error('خطأ في حذف المرتب:', err);
      alert('خطأ في حذف المرتب: ' + (err.response?.data?.message || err.message));
    }
  };

  useEffect(() => {
    if (token && role) fetchSalaries();
  }, [token, role]);

  if (!token) {
    return (
      <div className="login-container">
        <div className="login-box">
          <h1>تسجيل الدخول</h1>
          <div className="form-group">
            <label htmlFor="username">اسم المستخدم</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="أدخل اسم المستخدم"
              className={errors.username ? 'error' : ''}
            />
            {errors.username && <span className="error-message">{errors.username}</span>}
          </div>
          <div className="form-group">
            <label htmlFor="password">كلمة المرور</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="أدخل كلمة المرور"
              className={errors.password ? 'error' : ''}
            />
            {errors.password && <span className="error-message">{errors.password}</span>}
          </div>
          <button onClick={login}>تسجيل الدخول</button>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="dashboard" dir="rtl">
        <div className="header">
          <h1>نظام إدارة المرتبات</h1>
          <button className="logout-btn" onClick={logout}>تسجيل الخروج</button>
        </div>
        {role === 'admin' && (
          <div className="nav-links">
            <Link to="/create-user">إنشاء مستخدم</Link>
            <Link to="/create-salary">تسجيل مرتب</Link>
            <Link to="/">عرض المرتبات</Link>
          </div>
        )}
        <Routes>
          <Route
            path="/"
            element={
              <div className="section">
                <h2>المرتبات</h2>
                <div className="table-container">
                  <table>
                    <thead>
                      <tr>
                        <th>اسم الموظف</th>
                        <th>كود الموظف</th>
                        <th>القسم</th>
                        <th>الراتب الأساسي</th>
                        <th>بدل انتقال</th>
                        <th>بدل سكن</th>
                        <th>بدل وجبات</th>
                        <th>حوافز</th>
                        <th>خصومات</th>
                        <th>صافي المرتب</th>
                        <th>تاريخ التسجيل</th>
                        <th>ملاحظات</th>
                        <th>ملف</th>
                        {role === 'admin' && <th>إجراء</th>}
                      </tr>
                    </thead>
                    <tbody>
                      {salaries.map((salary) => (
                        <tr key={salary._id}>
                          <td>{salary.employeeName}</td>
                          <td>{salary.employeeCode}</td>
                          <td>{salary.department}</td>
                          <td>{salary.basicSalary}</td>
                          <td>{salary.transportationAllowance}</td>
                          <td>{salary.housingAllowance}</td>
                          <td>{salary.mealAllowance}</td>
                          <td>{salary.incentives}</td>
                          <td>{salary.deductions}</td>
                          <td>{salary.netSalary}</td>
                          <td>{new Date(salary.recordDate).toLocaleDateString('ar-EG')}</td>
                          <td>{salary.notes || 'لا يوجد'}</td>
                          <td>
                            {salary.filePath ? (
                              <a href={`http://54.227.197.41:5000/${salary.filePath}`} target="_blank" rel="noopener noreferrer">
                                تحميل
                              </a>
                            ) : 'لا يوجد'}
                          </td>
                          {role === 'admin' && (
                            <td>
                              <button className="delete-btn" onClick={() => deleteSalary(salary._id)}>حذف</button>
                            </td>
                          )}
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            }
          />
          {role === 'admin' && (
            <>
              <Route path="/create-user" element={<CreateUser token={token} />} />
              <Route path="/create-salary" element={<CreateSalary token={token} fetchSalaries={fetchSalaries} />} />
            </>
          )}
        </Routes>
      </div>
    </Router>
  );
};

export default App;
